  const removeItem = (item) => {
    // Your removeItem logic
  };

  const logout = () => {
    authLogout();
    setUser(null);
    // You should also clear the cart or any other user-specific state
  };

  return (
    <AppContext.Provider value={{ user, setUser, cart, addItem, removeItem, logout }}>
      {children}
