import { useState } from "react";
import Head from "next/head";
import AppContext from "../components/context";
import Layout from "../components/layout";

function MyApp({ Component, pageProps }) {
  // Define your state and updater functions here
  const [cart, setCart] = useState({ items: [], total: 0 });
  const [user, setUser] = useState(false);

  // Define the addItem function
  const addItem = (item) => {
    // Update the cart state
    // This is just a placeholder function, you'll need to implement actual logic
    setCart((currentCart) => {
      // Logic to add the item to the cart
      // For example, you might want to check if the item is already there and just increment the quantity
      return {
        ...currentCart,
        items: [...currentCart.items, item],
        total: currentCart.total + item.price, // Assuming the item has a price property
      };
    });
  };

  // Similarly define removeItem function
  const removeItem = (item) => {
    // Logic to remove the item from the cart
  };

  // Provide all state and functions in the context value
  return (
    <AppContext.Provider value={{ cart, addItem, removeItem, user, setUser }}>
      <Head>
        <link
          rel="stylesheet"
          href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css"
          integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm"
          crossOrigin="anonymous"
        />
      </Head>

      <Layout>
        <Component {...pageProps} />
      </Layout>
    </AppContext.Provider>
  );
}

export default MyApp;
