import React, { useState } from "react";
import { logout as authLogout } from './auth'; // Import the logout function

// create auth context with default value
const AppContext = React.createContext({
  isAuthenticated: false, 
  cart: { items: [], total: 0 }, 
  addItem: () => {},
  removeItem: () => {},
  user: null, 
  setUser: () => {},
  logout: () => {} // Add this line
});

export const AppProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [cart, setCart] = useState({ items: [], total: 0 });

  const addItem = (item) => {
    // Implementation for adding item to the cart
    // Update cart state accordingly
  };

  const removeItem = (item) => {
    // Implementation for removing item from the cart
    // Update cart state accordingly
  };


  // Now include the logout function in the provider
  const logout = () => {
    authLogout();
    setUser(null);
  };

  return (
    <AppContext.Provider value={{ user, setUser, logout, cart, addItem, removeItem }}>
    {children}
  </AppContext.Provider>
);
};

export default AppContext;
