import React, { useState, useContext } from "react";
import { useRouter } from 'next/router'; // Import useRouter for redirection
import { FormGroup, Label, Input, Modal, ModalBody } from "reactstrap"; // Import Modal components
import fetch from "isomorphic-fetch";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import CardSection from "./cardSection";
import AppContext from "./context";
import Cookies from "js-cookie";

function CheckoutForm() {
  const [data, setData] = useState({
    address: "",
    city: "",
    state: "",
  });
  const [error, setError] = useState("");
  const [showModal, setShowModal] = useState(false); // State to handle modal display
  const stripe = useStripe();
  const elements = useElements();
  const appContext = useContext(AppContext);
  const router = useRouter(); // For redirecting after order completion

  function onChange(e) {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  }
  async function submitOrder() {
    const cardElement = elements.getElement(CardElement);
    const token = await stripe.createToken(cardElement);
    const userToken = Cookies.get("token");

    if (token.error) {
      setError(token.error.message);
      return;
    }

    const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";

    const headers = {
      'Content-Type': 'application/json',
      ...(userToken && { Authorization: `Bearer ${userToken}` }),
    };

    const response = await fetch(`${API_URL}/orders`, {
      method: "POST",
      headers,
      body: JSON.stringify({
        amount: Number(Math.round(appContext.cart.total + "e2") + "e-2"),
        dishes: appContext.cart.items,
        address: data.address,
        city: data.city,
        state: data.state,
        token: token.token.id,
      }),
    });

    if (!response.ok) {
      const errorData = await response.text();
      setError(errorData);
    } else {
      console.log("Order successful!");
      setShowModal(true); // Show success modal
      setTimeout(() => {
        setShowModal(false); // Close modal after 5 seconds
        router.push('/'); // Redirect to the homepage
      }, 5000);
    }

  }

    // OTHER stripe methods you can use depending on app
    // // or createPaymentMethod - https://stripe.com/docs/js/payment_intents/create_payment_method
    // stripe.createPaymentMethod({
    //   type: "card",
    //   card: cardElement,
    // });

    // // or confirmCardPayment - https://stripe.com/docs/js/payment_intents/confirm_card_payment
    // stripe.confirmCardPayment(paymentIntentClientSecret, {
    //   payment_method: {
    //     card: cardElement,
    //   },
    // });


  return (
    <div className="paper">
      <h5>Your information:</h5>
      <hr />
      <FormGroup style={{ display: "flex" }}>
