import React from 'react';
import Head from "next/head";
import { AppProvider } from "../components/context"; // Import AppProvider
import Layout from "../components/layout";

// This is the root component of your Next.js application.
function MyApp({ Component, pageProps }) {
  // You don't need to define the cart or user state here, as they are handled within AppProvider

  return (
    // Wrap everything with AppProvider to make context available to all components
    <AppProvider> 
      <Head>
        {/* You can keep this Head component here for setting global tags like CSS */}
        <link
          rel="stylesheet"
          href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css"
          integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm"
          crossOrigin="anonymous"
        />
      </Head>
      {/* Layout component will wrap the entire application providing the navbar */}
      <Layout> 
        {/* This is where the page components will be rendered */}
        <Component {...pageProps} />
      </Layout>
    </AppProvider>
  );
}

export default MyApp;
